SELECT * FROM payment WHERE type_id = (SELECT id FROM paymenttype WHERE name = 'MONTHLY'); SELECT * FROM mark WHERE subject_id = (SELECT id FROM subject WHERE name = 'Art'); SELECT s.* from Student s INNER JOIN Payment p on s.id = p.student_id INNER JOIN ProductType pt on p.type_id = pt.id where pt.name = 'WEEKLY'; SELECT s.* from Student s INNER JOIN Mark m on s.id = m.student_id INNER JOIN Subject su on m.subject_id = su.id where su.name = 'Math';
