SELECT * from Student where id in (SELECT student_id FROM MARK GROUP BY student_id having AVG(mark)>8);SELECT id, name from Student where id in (select student_id from Mark GROUP BY student_id having MIN(mark)>7);SELECT id, name from Student where id in (select student_id from Payment WHERE YEAR(payment_date)=2019 GROUP BY student_id having count(student_id)>2)